    /** about page */
    "about.title": "Über mich",
    "about.businessIdentity.title": "Geschäftsidentität",
    "about.businessIdentity.description.prefix": "Dieses Portfolio ist mit",
    "about.businessIdentity.description.suffix":
      " verbunden, einem verifizierten Freelance-Softwareentwickler mit Sitz in Groß-Umstadt, Deutschland.",
    "about.businessIdentity.businessName": "Code & Cloud Lösungen",
    "about.businessIdentity.verified": "Verifiziert",
    "about.businessIdentity.profileDescription":
      "Freelance-Softwareentwickler in Groß-Umstadt. Verbinden Sie sich mit uns auf Google Business Profile für Bewertungen, Geschäftsinformationen und Standortdetails.",
    "about.businessIdentity.address.label": "Adresse:",
